#define HTML_SETTINGS_PAGE "<!DOCTYPE html><html lang=\"en\"><head><meta charset=\"utf-8\" /><meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\" /><meta name=\"viewport\" content=\"width=device-width, initial-scale=1, shrink-to-fit=no\" /><meta name=\"description\" content=\"\" /><meta name=\"author\" content=\"\" /><title>AirC Device | Settings</title><style>body{margin:0;background-color:#e6e7e8;font-family:'Roboto',sans-serif;font-size:16px;color:rgba(255,255,255,.85);letter-spacing:0.5px}.alert{position:absolute;bottom:20px;right:20px;padding:10px 20px;background:rgba(65, 64, 66, .4);transition:all 1s linear;-webkit-transition:all 1s linear;-o-transition:all 1s linear;-moz-transition:all 1s linear;visibility:collapse;opacity:0;display:none;border-radius:5px}.alert .alert-body{position:relative;font-size:16px;color:#e6e7e8}#success-alert{background:rgba(142,197,73,0.8)}#error-alert{background:rgba(197,73,73,0.8)}header{display:flex;flex-direction:row;flex-wrap:wrap;flex-basis:auto;justify-content:space-between;position:relative;z-index:999;height:80px;background-color:transparent}.brand-logo{width:auto;height:40px;line-height:90px;text-align:center;margin:15px 30px}.row{position:relative;display:flex;flex-direction:row;flex-wrap:wrap;flex-basis:auto}.widget{flex:1;margin:40px 20px 0 20px;-webkit-transition:all 0.3s ease;-moz-transition:all 0.3s ease;-o-transition:all 0.3s ease;transition:all 0.3s ease}.widget-header{padding:0 15px;line-height:60px;font-size:16px;font-weight:600;color:#414042;text-align:center;position:relative}.widget-header svg{position:absolute;top:0;right:0}.widget-body{padding:15px}.form-group{margin-bottom:60px}.form-group label{color:#414042;font-size:14px;text-transform:uppercase;letter-spacing:1px;font-weight:600;margin-bottom:10px;display:inline-block}.form-group input{display:block;width:98%;padding:10px 1%;font-size:14px;font-weight:400;line-height:1.5;background-color:transparent;background-clip:padding-box;border:0px solid rgba(65, 64, 66, .4);transition:background-color .15s ease-in-out, box-shadow .15s ease-in-out;-webkit-transition:background-color .15s ease-in-out, box-shadow .15s ease-in-out;-o-transition:background-color .15s ease-in-out, box-shadow .15s ease-in-out;-moz-transition:background-color .15s ease-in-out, box-shadow .15s ease-in-out;color:#414042 !important;border-radius:0 !important;overflow:visible;box-shadow:0 2px 0 0 #bcbec0}.form-group input:focus{outline:0 !important;box-shadow:0 2px 0 0 #bcbec0}.form-group button{margin-top:0px;background:transparent;border:none;border-radius:0;padding:10px 40px;font-size:14px;font-weight:600;text-transform:uppercase;color:#414042;cursor:pointer;position:relative;border:2px solid #e6e7e8;-webkit-transition:border-color 0.2s ease;-moz-transition:border-color 0.2s ease;-o-transition:border-color 0.2s ease;transition:border-color 0.2s ease}.form-group button svg{position:absolute;z-index:1}.form-group button:hover{border-color:#ef5b40}.loader{position:relative;margin-left:55px;width:30px;height:30px;display:none;visibility:collapse;opacity:0}.box{position:absolute;width:15px;height:15px;background:#ef5b40}.box:nth-child(1){animation:a1 2s infinite linear}.box:nth-child(2){animation:a2 2s infinite linear}@keyframes a1{0%{top:0;left:0;width:15px;height:15px}12.5%{top:0;left:0;width:30px;height:15px}25%{top:0;left:0;width:30px;height:15px}37.5%{top:0;left:15px;width:15px;height:15px}50%{top:0;left:15px;width:15px;height:15px}62.5%{top:0;left:15px;width:15px;height:30px}75%{top:0;left:15px;width:15px;height:30px}87.5%{top:15px;left:15px;width:15px;height:15px}100%{top:15px;left:15px;width:15px;height:15px}}@keyframes a2{0%{right:0;bottom:0;width:15px;height:15px}12.5%{right:0;bottom:0;width:15px;height:15px}25%{right:0;bottom:0;width:30px;height:15px}37.5%{right:0;bottom:0;width:30px;height:15px}50%{right:15px;bottom:0;width:15px;height:15px}62.5%{right:15px;bottom:0;width:15px;height:15px}75%{right:15px;bottom:0;width:15px;height:30px}87.5%{right:15px;bottom:0;width:15px;height:30px}100%{right:15px;bottom:15px;width:15px;height:15px}}footer{position:absolute;bottom:0;right:0}</style></head><body><div class=\"alert\" id=\"success-alert\"><div class=\"alert-body\"></div></div><div class=\"alert\" id=\"error-alert\"><div class=\"alert-body\"></div></div> <header><div class=\"logo\"> <img src=\"data:image/png;base64,\" class=\"brand-logo\"></div> </header><div class=\"row\"><div class=\"widget\"><div class=\"widget-header\"> <svg viewBox=\"0 0 100 100\" width=\"50px\" height=\"60px\"> <path d=\"M98,35 L98,2 L65,2\" fill=\"none\" stroke=\"#ef5b40\" stroke-width=\"5\" /> </svg> Network Configuration</div><div class=\"widget-body\"><form onsubmit=\"network_form(event);\"><div class=\"form-group\"> <label for=\"ssid\">SSID:</label> <input type=\"text\" id=\"ssid\" placeholder=\"Network SSID\" maxlength=\"32\" autocomplete=\"off\" required></div><div class=\"form-group\"> <label for=\"ssid\">Password:</label> <input type=\"password\" id=\"pass\" minlength=\"8\" maxlength=\"64\" placeholder=\"&bull;&bull;&bull;&bull;&bull;&bull;&bull;&bull;\" autocomplete=\"off\" required></div><div class=\"form-group\"> <button type=\"submit\" id=\"network_form_button\"> <svg viewBox=\"0 0 100 100\" width=\"50\" height=\"42\" style=\"top: -2px; right: -6px;\"> <path d=\"M98,25 L98,2 L75,2\" fill=\"none\" stroke=\"#ef5b40\" stroke-width=\"5\" /> </svg> <svg viewBox=\"0 0 100 100\" width=\"50\" height=\"42\" style=\"bottom: -2px; left: -6px;\"> <path d=\"M2,75 L2,98 L25,98\" fill=\"none\" stroke=\"#ef5b40\" stroke-width=\"5\" /> </svg> Change </button><div class=\"loader\" id=\"network_form_loader\"><div class=\"box\"></div><div class=\"box\"></div></div></div></form></div></div></div> <script>function network_form(event){var button=document.getElementById(\"network_form_button\");button.style.display=\"none\";button.style.visibility=\"collapse\";button.style.opacity=0;var loader=document.getElementById(\"network_form_loader\");loader.style.display=\"block\";loader.style.visibility=\"visible\";loader.style.opacity=100;var params=\"ssid=\"+document.getElementById(\"ssid\").value+\"&pass=\"+document.getElementById(\"pass\").value;var xhr=new XMLHttpRequest();xhr.onreadystatechange=function(){if(xhr.readyState===4&&xhr.status===200){if(xhr.responseText===\"OK\"){var success_alert=document.getElementById(\"success-alert\");success_alert.getElementsByClassName(\"alert-body\")[0].innerHTML=\"Network configuration changed successfully!\";success_alert.style.display=\"block\";success_alert.style.visibility=\"visible\";success_alert.style.opacity=100;setTimeout(function(){success_alert.style.display=\"none\";success_alert.style.visibility=\"collapse\";success_alert.style.opacity=0;},3000);} if(xhr.responseText===\"ERROR\"){var error_alert=document.getElementById(\"error-alert\");error_alert.getElementsByClassName(\"alert-body\")[0].innerHTML=\"Network configuration change failed!\";error_alert.style.display=\"block\";error_alert.style.visibility=\"visible\";error_alert.style.opacity=100;setTimeout(function(){error_alert.style.display=\"none\";error_alert.style.visibility=\"collapse\";error_alert.style.opacity=0;},3000);} loader.style.display=\"none\";loader.style.visibility=\"collapse\";loader.style.opacity=0;button.style.display=\"block\";button.style.visibility=\"visible\";button.style.opacity=100;}};xhr.open(\"POST\",\"network\",true);xhr.send(params);event.preventDefault();};</script> </body></html>"

